    // form_data.append('senha', user.senha)
    // form_data.append('loja', user.loja)

    if(user.name === '' && user.senha === '' && user.loja === ''){
        Api.post('cad.php', '').then(response=>{
            console.log(response)
        })
    }else{
        Api.post('cad.php', form_data).then(response=>{
            console.log(response)
        })
    }
    

    
}

