import React from 'react';
import whiteLogo from '../../styles/images/whiteLogo.png';
import { Input, Button } from 'antd';
import { AiOutlineUser, AiFillLock } from 'react-icons/ai'
import 'antd/dist/antd.css'


const style = {
    main: {
        display: 'flex',
        height: '100%',
        width: '100%',
        background: '#ff773b'
    },
    divInfo: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100%',
        width: '60%'
    },
    sectionInfo: {
        height: '100%',
        width: '100%',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        flexDirection: 'column',
        color: '#ffffff'
    },
    divLogin: {
        height: '100%',
        width: 'auto',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
    },
    sectionLogin: {
        height: '486px',
        width: 'auto',
        minWidth: '522px',
        minHeight: '486px',
        background: '#ff882a',
        borderRadius: '10px',
        boxShadow: '#fd6f41 0px 0px 86px 16px',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center'
    },
    input: {
        width: '300px',
        marginBottom: '13px'
    },
    divBack:{
        background:'url(`../../styles/images/whiteLogo.png`)'
    }
}

const Login = () => {
    return (
        <main style={style.main}>
            <div style={style.divInfo}>
                <section style={style.sectionInfo}>
                    <h1 style={{ color: '#fff', fontFamily: 'Lato', fontSize: '25px', fontWeight: 'lighter', WebkitFontSmoothing: 'antialiased' }}>Gerencie o conteúdo que sera mostrado a seus clientes</h1>
                </section>
            </div>
            <div style={style.divLogin}>
                <section style={style.sectionLogin}>
                    <div style={style.divBack}></div>
                    <Input style={style.input} size="large" placeholder="Usuário" prefix={<AiOutlineUser />} />
                    <Input.Password style={style.input} size="large" placeholder="Senha:" prefix={<AiFillLock />} />
                    <Button type='default'>Entrar</Button>
                </section>
            </div>
        </main>
    )
}

export default Login
