import React from 'react';
import 'primereact/resources/themes/saga-blue/theme.css';
import 'primereact/resources/primereact.min.css';
import '../../styles/css/layout.css';
import { Typography } from 'antd';
import 'antd/dist/antd.css';
import { FiHome, FiImage } from 'react-icons/fi/index';
import { CgScreen } from 'react-icons/cg/index'
import whiteLogo from '../../styles/images/whiteLogo.png';
import icone from '../../styles/images/icone.png';

const { Title } = Typography

const style = {
    main: {
        height: '100%',
        width: '100%',
        background: '#fff',
        display: 'flex',
        flexDirection: 'column'
    },
    sidebar: {
        height: '100%',
        width: '200px',
        background: 'linear-gradient(90deg,#ff773b,#ff8e24 35%,#ff8e24)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'space-between'
    },
    sidebarCol: {
        height: '100%',
        width: '72px',
        background: 'linear-gradient(90deg,#ff773b,#ff8e24 35%,#ff8e24)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'space-between'
    },
    listLi: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        width: '100%',
        marginBottom: '15px',
        height: '40px'
    },
    iconMenu: {
        marginBottom: '10px',
        fontSize: '20px',
        color: 'white',
        marginRight: '10px'
    }
}

const ListNotCol = () => {
    return (
        <>
            <li style={style.listLi}><FiHome style={style.iconMenu} /><Title level={5} style={{ color: 'white' }}>Home</Title></li>
            <li style={style.listLi}><CgScreen style={style.iconMenu} /><Title level={5} style={{ color: 'white' }}>Telas</Title></li>
            <li style={style.listLi}><FiImage style={style.iconMenu} /><Title level={5} style={{ color: 'white' }}>Conteúdos</Title></li>
        </>
    )
}

const ListCol = () => {
    return (
        <>
            <li style={style.listLi}><FiHome style={{ marginBottom: '10px', fontSize: '20px', color: 'white' }} /></li>
            <li style={style.listLi}><CgScreen style={{ marginBottom: '10px', fontSize: '20px', color: 'white' }} /></li>
            <li style={style.listLi}><FiImage style={{ marginBottom: '10px', fontSize: '20px', color: 'white' }} /></li>
        </>
    )
}

const Layout = (props) => {
    const [collapsed, setCollapsed] = React.useState(false)

    return (
        <main style={style.main}>
            <div style={collapsed ? style.sidebarCol : style.sidebar}>
                <div id='menulogo' style={{ display: 'flex', justifyContent: 'center', width: '100%' }}>
                    {
                        collapsed ? <img src={icone} style={{ width: '57px', marginTop: '4px', marginBottom: '10px' }} alt="CNX Telecom - você sempre conectado!" />
                            : <img src={whiteLogo} style={{ width: '120px', marginTop: '4px', marginBottom: '10px' }} alt="CNX Telecom - você sempre conectado!" />
                    }
                </div>
                <div id='menu' style={{ height: '860px', width: '100%' }}>
                    <ul style={{ listStyleType: 'none', display: 'flex', justifyContent: 'center', flexDirection: 'column', marginTop: '20px' }}>
                        {
                            collapsed ? <ListCol /> : <ListNotCol />
                        }
                    </ul>
                </div>
                <div id='menufooter' style={{ height: '40px', width: '100%', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                    <Title level={5} style={{ fontSize: '20px', color: 'white', cursor: 'pointer' }} onClick={() => { setCollapsed(!collapsed) }}>{collapsed ? '>' : '<'}</Title>
                </div>
            </div>
        </main>
    )
}

export default Layout;
